for(var i = 0; i < posts.length; i++) {
  var card = document.createElement('div');
  card.className = 'intro-card';
  
  var title = document.createElement('h1');
  title.className = 'card-title';
  title.innerHTML = posts[i].title;
  card.appendChild(title);

  var date = document.createElement('h3');
  date.className = 'card-date';
  date.innerHTML = posts[i].date;
  card.appendChild(date);

  var intro = document.createElement('div');
  intro.style.maxHeight = '200px';
  intro.style.overflow = 'hidden';

  var p = document.createElement('p');
  p.className = 'card-content';
  for (var j = 0; j < posts[i].content.length; j++) {
    p.innerHTML = p.innerHTML + (j !== 0 ? ' ' : '') + posts[i].content[j];
    intro.appendChild(p);
  }

  card.appendChild(intro);

  var button = document.createElement('button');
  button.className = 'read-more';
  button.innerHTML = 'Continue reading'
  card.appendChild(button);

  contents.appendChild(card);
}
